const handlers={toggleNavBarBurger:()=>{const e=Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0);e.length>0&&e.forEach(e=>{e.addEventListener("click",()=>{let t=e.dataset.target;t=document.getElementById(t),e.classList.toggle("is-active"),t.classList.toggle("is-active")})})},deleteArticle:()=>{$(".delete-article").on("click",e=>{$target=$(e.target);const t=$target.attr("data-id");$.ajax({type:"DELETE",url:`/data/${t}`,success:e=>{alert("Deleting Article"),window.location.href="/"},error:e=>{console.log(e)}})})},updateEntry:()=>{$(".update-entry").on("click",e=>{const t=$(e.currentTarget).attr("data-date"),a=window.location.pathname;$(e.currentTarget).addClass("is-loading"),$.ajax({type:"POST",url:`${a}/${t}`,success:e=>{window.location.reload()},error:e=>{console.log(e),window.location.reload()}})})},dismissNotification:()=>{$(".messages").on("click",e=>{0===$(e.target).parent().siblings().length?$(e.currentTarget).addClass("is-hidden").remove():$(e.target).parent().addClass("is-hidden").remove()})}};document.addEventListener("DOMContentLoaded",()=>{handlers.toggleNavBarBurger(),handlers.deleteArticle(),handlers.dismissNotification(),handlers.updateEntry()});