const handlers={toggleNavBarBurger:()=>{const e=Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0);e.length>0&&e.forEach(e=>{e.addEventListener("click",()=>{let t=e.dataset.target;t=document.getElementById(t),e.classList.toggle("is-active"),t.classList.toggle("is-active")})})},updateEntry:()=>{$(".update-entry").on("click",e=>{const t=$(e.currentTarget).attr("data-date"),a=window.location.pathname;$(e.currentTarget).addClass("is-loading"),$.ajax({type:"POST",url:`${a}/${t}`,success:e=>{window.location.reload()},error:e=>{console.log(e),window.location.reload()}})})},dismissMessagesNotification:()=>{$(".messages").on("click",e=>{0===$(e.target).parent().siblings().length?$(e.currentTarget).addClass("is-hidden").remove():$(e.target).parent().addClass("is-hidden").remove()})},dismissNotification:()=>{$(".footer-notification").on("click",e=>{$(e.currentTarget).addClass("is-hidden").remove()})}};document.addEventListener("DOMContentLoaded",()=>{handlers.toggleNavBarBurger(),handlers.dismissMessagesNotification(),handlers.dismissNotification(),handlers.updateEntry()});